import React, { useEffect, useState, useMemo } from "react";
import { Pokemon, getAll, getByName } from "./API";

import "./styles.css";


interface PokemonWithPower extends Pokemon {
  power: number
}

const calculatePower = (pokemon: Pokemon) =>
  pokemon.hp +
  pokemon.attack +
  pokemon.defense +
  pokemon.special_attack +
  pokemon.special_defense +
  pokemon.speed;


const PokemonTable: React.FunctionComponent<{
  pokemon: PokemonWithPower[];
}> = ({ pokemon }) => {
  return (
    <table>
      <thead>
        <tr>
          <td>ID</td>
          <td>Name</td>
          <td>Type</td>
          <td colSpan={6}>Stats</td>
          <td>Power</td>
        </tr>
      </thead>
      <tbody>
        {pokemon.map((p) => (
          <tr key={p.id}>
            <td>{p.id}</td>
            <td>{p.name}</td>
            <td>{p.type.join(",")}</td>
            <td>{p.hp}</td>
            <td>{p.attack}</td>
            <td>{p.defense}</td>
            <td>{p.special_attack}</td>
            <td>{p.special_defense}</td>
            <td>{p.speed}</td>
            <td>{p.power}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default function App() {
  const [pokemon, setPokemon] = useState<Pokemon[]>([]);

  useEffect(() => {
    getAll().then(setPokemon)
  }, [])

  const pokemonWithPower = pokemon.map((p) => ({ ...p, power: calculatePower(p) }))
  const [threshold, setThreshold] = useState(0);
  const onSetThreshold = (evt: React.ChangeEvent<HTMLInputElement>) => setThreshold(parseInt(evt.target.value, 10))
  const countOverThreshold = pokemonWithPower.filter((p) => p.power > threshold).length


  return ( 
    <div>
      <div className="top-bar">

        <div>Search</div>
        <input type="text"></input>

        <div>Power threshold</div>
        <input value={threshold} onChange={onSetThreshold}></input>

        <div>Count over threshold: </div>

      </div>
      <div className="two-column">
        <PokemonTable pokemon={pokemonWithPower} />
        <div>
          <div>Min: </div>
          <div>Max: </div>
        </div>
      </div>
    </div>
  );
}
